    // Convert string payload to JSON if needed
    let data = msg.payload;
    if (typeof data === 'string') {
        data = JSON.parse(data);
    }

    // Process sensor values
    const parsed = {
        timestamp: data.timestamp || "N/A",
        counter: Number(data.counter) || 0,
        temperature: Math.round(Number(data.temperature) * 100) / 100 || 0,
        humidity: Math.round(Number(data.humidity) * 100) / 100 || 0,
        voltage: Math.round(Number(data.voltage) * 100) / 100 || 0,
        pressure: Math.round(Number(data.pressure) * 100) / 100 || 0,
        water_temp: Math.round(Number(data.water_temp) * 100) / 100 || 0,
        discharge: Math.round(Number(data.discharge) * 100) / 100 || 0,
        soil: Array.isArray(data.soil) && data.soil.length > 0 ? 
        Math.min(...data.soil) :  (Number(data.soil) || 0),
        battery: Array.isArray(data.battery) && data.battery.length > 0 ? 
        Math.min(...data.battery) : (Number(data.battery) || 0)
    };

    // Add site from topic (drip/<site>/data)
    const topicParts = msg.topic.split('/');
    parsed.site = topicParts[1] || "unknown";

    // Debug parsed output
    node.warn("Parsed data: " + JSON.stringify(parsed));

    // Update message payload
    msg.payload = parsed;
    return msg.payload;
} catch (e) {
    node.error("Parser error: " + e.message, msg);
    return msg.payload;
    //return NULL;
}

