    if (typeof data === 'string') {
        data = JSON.parse(data);
    }

    // Extract status information
    const statusInfo = {
        message: data.message || "No message",
        device: data.device || "Unknown",
        timestamp: data.timestamp || "N/A",
        site: data.site || "Unknown",
        version: data.version || "N/A"
    };

    // Store in flow context for display
    flow.set('lastStatus', statusInfo);

    node.warn("Status received: " + statusInfo.message);

    msg.payload = statusInfo;
    return msg;
} catch (e) {
    node.error("Status parser error: " + e.message, msg);
    return null;
